namespace Mageplaza\Membership\Block\Adminhtml\Grid\Column\Renderer;

use Magento\Backend\Block\Context;
use Magento\Backend\Block\Widget\Grid\Column\Renderer\AbstractRenderer;
use Magento\Framework\DataObject;
use Mageplaza\Membership\Helper\Data;

/**
 * Class Duration
 * @package Mageplaza\Membership\Block\Adminhtml\Grid\Column\Renderer
 */
class Duration extends AbstractRenderer
{
    /**
     * @var Data
     */
    protected $helper;

    /**
     * Duration constructor.
     *
     * @param Context $context
     * @param Data $helper
     * @param array $data
     */
    public function __construct(
        Context $context,
        Data $helper,
        array $data = []
    ) {
        $this->helper = $helper;

        parent::__construct($context, $data);
    }

    /**
     * @param DataObject $row
     *
     * @return string
     */
    public function render(DataObject $row)
    {
        $duration = $this->helper->getDurationText($row->getData('membership_data'));

        $row->setData('history_duration', $duration);

        return parent::render($row);
    }
}
